UPDATE feats SET name = 'Competencia con Armadura (Ligera)', benefit = 'Cuando llevas un tipo de armadura con el que eres competente, el penalizador de armadura solo se aplica a las pruebas de Equilibrio, Trepar, Escapismo, Esconderse, Saltar, Moverse Sigilosamente, Juego de Manos y Piruetas.', normal = 'Un personaje que lleva una armadura con la que no es competente aplica su penalizador de armadura a las tiradas de ataque y a todas las pruebas de habilidad que impliquen movimiento, incluyendo Montar.', special = ARRAY['Todos los personajes excepto magos, hechiceros y monjes tienen automáticamente Competencia con Armadura (Ligera) como dote adicional. No necesitan seleccionarla.'], source_book = 'Manual del Jugador 1' WHERE slug = 'competencia-armadura-ligera';
