-- ENDURANCE -> Aguante
UPDATE feats SET name = 'Aguante', benefit = 'Ganas un bonificador +4 en las siguientes pruebas y salvaciones: pruebas de Nadar para resistir daño no letal, pruebas de Constitución para seguir corriendo, pruebas de Constitución para evitar daño no letal por una marcha forzada, pruebas de Constitución para aguantar la respiración, pruebas de Constitución para evitar daño no letal por hambre o sed, salvaciones de Fortaleza para evitar daño no letal por entornos cálidos o fríos, y salvaciones de Fortaleza para resistir daño por asfixia. Además, puedes dormir con armadura ligera o media sin fatigarte.', normal = 'Un personaje sin esta dote que duerme con armadura media o pesada está automáticamente fatigado al día siguiente.', special = ARRAY['Un explorador gana automáticamente Aguante como dote adicional a nivel 3. No necesita seleccionarla.'], source_book = 'Manual del Jugador 1' WHERE slug = 'aguante';
DELETE FROM feats WHERE (name = 'Endurance' OR slug = 'endurance') AND EXISTS (SELECT 1 FROM feats WHERE slug = 'aguante');
UPDATE feats SET name = 'Aguante', slug = 'aguante', benefit = 'Ganas un bonificador +4 en las siguientes pruebas y salvaciones: pruebas de Nadar para resistir daño no letal, pruebas de Constitución para seguir corriendo, pruebas de Constitución para evitar daño no letal por una marcha forzada, pruebas de Constitución para aguantar la respiración, pruebas de Constitución para evitar daño no letal por hambre o sed, salvaciones de Fortaleza para evitar daño no letal por entornos cálidos o fríos, y salvaciones de Fortaleza para resistir daño por asfixia. Además, puedes dormir con armadura ligera o media sin fatigarte.', normal = 'Un personaje sin esta dote que duerme con armadura media o pesada está automáticamente fatigado al día siguiente.', special = ARRAY['Un explorador gana automáticamente Aguante como dote adicional a nivel 3. No necesita seleccionarla.'], source_book = 'Manual del Jugador 1' WHERE name = 'Endurance' OR slug = 'endurance';

-- ENLARGE SPELL -> Ampliar Conjuro
UPDATE feats SET name = 'Ampliar Conjuro', benefit = 'Puedes alterar un hechizo con un alcance de corto, medio o largo para aumentar su alcance en un 100%. Un hechizo ampliado con un alcance de corto ahora tiene un alcance de 50 pies + 5 pies/nivel, mientras que los hechizos de alcance medio tienen un alcance de 200 pies + 20 pies/nivel y los hechizos de alcance largo tienen un alcance de 800 pies + 80 pies/nivel. Un hechizo ampliado usa un espacio de hechizo un nivel superior al nivel real del hechizo. Los hechizos cuyos alcances no están definidos por distancia, así como los hechizos cuyos alcances no son corto, medio o largo, no tienen alcances aumentados.', source_book = 'Manual del Jugador 1' WHERE slug = 'ampliar-conjuro';
DELETE FROM feats WHERE (name = 'Enlarge Spell' OR slug = 'enlarge-spell') AND EXISTS (SELECT 1 FROM feats WHERE slug = 'ampliar-conjuro');
