-- WEAPON FINESSE -> Sutileza con las Armas
UPDATE feats SET name = 'Sutileza con las Armas', benefit = 'Con un arma ligera, estoque, látigo o cadena armada hecha para una criatura de tu categoría de tamaño, puedes usar tu modificador de Destreza en lugar de tu modificador de Fuerza en las tiradas de ataque. Si llevas un escudo, su penalizador de armadura se aplica a tus tiradas de ataque.', special = ARRAY['Un guerrero puede seleccionar Sutileza con las Armas como una de sus dotes adicionales de guerrero. Las armas naturales siempre se consideran armas ligeras.'], prerequisites = '["Ataque base +1"]', source_book = 'Manual del Jugador 1' WHERE slug = 'sutileza-con-las-armas';
DELETE FROM feats WHERE (name = 'Weapon Finesse' OR slug = 'weapon-finesse') AND EXISTS (SELECT 1 FROM feats WHERE slug = 'sutileza-con-las-armas');
UPDATE feats SET name = 'Sutileza con las Armas', slug = 'sutileza-con-las-armas', benefit = 'Con un arma ligera, estoque, látigo o cadena armada hecha para una criatura de tu categoría de tamaño, puedes usar tu modificador de Destreza en lugar de tu modificador de Fuerza en las tiradas de ataque. Si llevas un escudo, su penalizador de armadura se aplica a tus tiradas de ataque.', special = ARRAY['Un guerrero puede seleccionar Sutileza con las Armas como una de sus dotes adicionales de guerrero. Las armas naturales siempre se consideran armas ligeras.'], prerequisites = '["Ataque base +1"]', source_book = 'Manual del Jugador 1' WHERE name = 'Weapon Finesse' OR slug = 'weapon-finesse';

