
-- ============================================
-- TABLA DE CONTENIDOS DE LIBROS
-- Referencias a páginas específicas de cada libro
-- ============================================

CREATE TABLE IF NOT EXISTS public.book_contents (
  id BIGSERIAL PRIMARY KEY,
  book_slug TEXT NOT NULL REFERENCES public.books(slug) ON DELETE CASCADE,
  content_type TEXT NOT NULL, -- 'chapter', 'class', 'race', 'feat', 'skill', 'spell', 'equipment', 'monster', 'magic_item'
  title TEXT NOT NULL,
  page_number INTEGER NOT NULL,

  -- Enlaces opcionales a entradas existentes en otras tablas
  class_slug TEXT REFERENCES public.classes(slug),
  race_slug TEXT REFERENCES public.races(slug),
  feat_slug TEXT REFERENCES public.feats(slug),
  skill_slug TEXT REFERENCES public.skills(slug),
  spell_slug TEXT REFERENCES public.spells(slug),

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Índices
CREATE INDEX IF NOT EXISTS idx_book_contents_book ON public.book_contents(book_slug);
CREATE INDEX IF NOT EXISTS idx_book_contents_type ON public.book_contents(content_type);
CREATE INDEX IF NOT EXISTS idx_book_contents_page ON public.book_contents(page_number);

-- Comentarios
COMMENT ON TABLE public.book_contents IS 'Índice de contenidos de todos los libros D&D 3.5';
COMMENT ON COLUMN public.book_contents.content_type IS 'Tipo de contenido: chapter, class, race, feat, skill, spell, equipment, monster, magic_item';
COMMENT ON COLUMN public.book_contents.page_number IS 'Número de página en el PDF del libro';


