
-- ============================================
-- TABLA DE CONTENIDOS DE LIBROS
-- Referencias a páginas específicas de cada libro
-- ============================================

DROP TABLE IF EXISTS public.book_contents CASCADE;

CREATE TABLE public.book_contents (
  id BIGSERIAL PRIMARY KEY,
  book_slug TEXT NOT NULL REFERENCES public.books(slug) ON DELETE CASCADE,
  content_type TEXT NOT NULL, -- 'chapter', 'class', 'race', 'feat', 'skill', 'spell', 'equipment', 'monster', 'magic_item', 'other'
  title TEXT NOT NULL,
  page_number INTEGER NOT NULL,
  source_page INTEGER, -- Página del PDF donde se encuentra el índice

  -- Enlaces opcionales a entradas existentes en otras tablas
  class_slug TEXT REFERENCES public.classes(slug),
  race_slug TEXT REFERENCES public.races(slug),
  feat_slug TEXT REFERENCES public.feats(slug),
  skill_slug TEXT REFERENCES public.skills(slug),
  spell_slug TEXT REFERENCES public.spells(slug),

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW(),

  -- Índice único por libro, tipo y título
  UNIQUE(book_slug, content_type, title)
);

-- Índices para consultas rápidas
CREATE INDEX idx_book_contents_book ON public.book_contents(book_slug);
CREATE INDEX idx_book_contents_type ON public.book_contents(content_type);
CREATE INDEX idx_book_contents_page ON public.book_contents(page_number);
CREATE INDEX idx_book_contents_title ON public.book_contents USING gin(to_tsvector('english', title));

-- Comentarios
COMMENT ON TABLE public.book_contents IS 'Índice de contenidos de todos los libros D&D 3.5 extraído de los PDFs';
COMMENT ON COLUMN public.book_contents.content_type IS 'Tipo de contenido: chapter, class, race, feat, skill, spell, equipment, monster, magic_item, other';
COMMENT ON COLUMN public.book_contents.page_number IS 'Número de página donde comienza el contenido en el PDF del libro';
COMMENT ON COLUMN public.book_contents.source_page IS 'Número de página del PDF donde se encontró esta entrada del índice';


-- Contenidos de Player's Handbook (123 entradas)
ON CONFLICT (book_slug, content_type, title) DO UPDATE
  SET page_number = EXCLUDED.page_number,
      source_page = EXCLUDED.source_page,
      updated_at = NOW();


-- Contenidos de Dungeon Master's Guide (67 entradas)
ON CONFLICT (book_slug, content_type, title) DO UPDATE
  SET page_number = EXCLUDED.page_number,
      source_page = EXCLUDED.source_page,
      updated_at = NOW();


-- Contenidos de Monster Manual (121 entradas)
ON CONFLICT (book_slug, content_type, title) DO UPDATE
  SET page_number = EXCLUDED.page_number,
      source_page = EXCLUDED.source_page,
      updated_at = NOW();
